radContent.score.rule:
(profile, entity, cardData) => {
  const {
    entitlementData: {
      current = {},
      transitionable = {},
    } = {},
    gem: {
      subscriberCount = 0
    } = {}
  } = entity;

  if (!current['gem'] && !Object.keys(transitionable['gem'] || {}).length) {
    return -1
  }

  if (subscriberCount > 0) {
    return 0;
  }

  const { clicked } = cardData;
  const nowDate = new Date();
  if (clicked) {
    const oneDay = 24*60*60*1000; // hours*minutes*seconds*milliseconds
    const lastClickedDate = new Date(clicked);
    const daysSinceClicked = Math.round(Math.abs((nowDate.getTime() - lastClickedDate.getTime())/(oneDay)));

    if (typeof daysSinceClicked === 'number' && daysSinceClicked < 7) {
      return 0;
    }
  }

  return 10;
}

